import { getCategoryByType } from '@/api/category'
import {
  getSetmealPage,
  enableOrDisableSetmeal,
  deleteSetmeal,
} from '@/api/setMeal'
import { ElSelect } from 'element-ui/types/select'

export default {
  //model data
  data() {
    return {
      name: '', //setmeal name,
      page: 1,
      pageSize: 10,
      total: 0,
      records: [],
      options: [],
      categoryId: '',
      statusArr: [
        {
          value: '0',
          label: 'Off-Sale',
        },
        {
          value: '1',
          label: 'On-Sale',
        },
      ],
      status: '', //sale status
      multipleSelection: '', //multiple elements selected in current form
    }
  },
  created() {
    getCategoryByType({ type: 2 }).then((res) => {
      if (res.data.code === 1) {
        this.options = res.data.data
      }
    })

    this.pageQuery()
  },
  methods: {
    //pagination query
    pageQuery() {
      const params = {
        page: this.page,
        pageSize: this.pageSize,
        name: this.name,
        status: this.status,
        categoryId: this.categoryId,
      }
      getSetmealPage(params).then((res) => {
        if (res.data.code === 1) {
          this.total = res.data.data.total
          this.records = res.data.data.records
        }
      })
    },
    handleSizeChange(pageSize) {
      this.pageSize = pageSize
      this.pageQuery()
    },
    handleCurrentChange(page) {
      this.page = page
      this.pageQuery()
    },
    handleStartOrStop(row) {
      const p = {
        id: row.id,
        status: !row.status ? 1 : 0,
      }

      this.$confirm(
        'Confirm to adjust the sales status of the current setmeal, do you want to continue?',
        'Prompt',
        {
          confirmButtonText: 'Confirm',
          cancelButtonText: 'Cancel',
          type: 'warning',
        }
      ).then(() => {
        enableOrDisableSetmeal(p).then((res) => {
          if (res.data.code === 1) {
            this.$message.success('Modified the sale status successfully!')
            this.pageQuery()
          }
        })
      })
    },
    handleDelete(type: string, id: string) {
      this.$confirm(
        'Confirm to delete current setmeal, do you want to continue?',
        'Prompt',
        {
          confirmButtonText: 'Confirm',
          cancelButtonText: 'Cancel',
          type: 'warning',
        }
      ).then(() => {
        let param = ''
        if (type === 'b') {
          //delete in batches
          const arr = new Array()
          this.multipleSelection.forEach((element) => {
            //1, 2, 3
            arr.push(element.id)
          })
          param = arr.join(',')
        } else {
          //single delete
          param = id
        }
        deleteSetmeal(param).then((res) => {
          if (res.data.code === 1) {
            this.$message.success('Deleted Successfully!')
          } else {
            this.$message.error(res.data.msg)
          }
        })
      })
    },
    handleSelectionChange(val) {
      this.multipleSelection = val
    },
  },
}
