import {getCategoryByType} from '@/api/category'
import {getSetmealPage, enableOrDisableSetmeal} from '@/api/setMeal'

export default {
  //model data
  data() {
    return {
      name: '',//setmeal name,
      page: 1,
      pageSize: 10,
      total: 0,
      records: [],
      options: [],
      categoryId: '', 
      statusArr: [
        {
          value: '0', 
          label: 'Off-Sale'
        }, 
        {
          value: '1', 
          label: 'On-Sale'
        }
      ], 
      status: '',//sale status
    }
  },
  created() {
    getCategoryByType({type: 2}).then(res => {
      if(res.data.code === 1) {
        this.options = res.data.data
      }
    })

    this.pageQuery()
  }, 
  methods: {
    //pagination query 
    pageQuery() {
      const params = {
        page: this.page, 
        pageSize: this.pageSize, 
        name: this.name, 
        status: this.status, 
        categoryId: this.categoryId
      }
      getSetmealPage(params).then(res => {
        if(res.data.code === 1) {
          this.total = res.data.data.total
          this.records = res.data.data.records
        }
      })
    }, 
    handleSizeChange(pageSize) {
      this.pageSize = pageSize
      this.pageQuery()
    }, 
    handleCurrentChange(page) {
      this.page = page
      this.pageQuery()
    }, 
    handleStartOrStop(row) {
      const p = {
        id: row.id, 
        status: !row.status ? 1 : 0
      }

      this.$confirm('Confirm to adjust the sales status of the current setmeal, do you want to continue?', 'Prompt', {
          confirmButtonText: 'Confirm',
          cancelButtonText: 'Cancel',
          type: 'warning'
        }).then(() => {
          enableOrDisableSetmeal(p).then(res => {
            if(res.data.code === 1) {
              this.$message.success('Modified the sale status successfully!')
              this.pageQuery()
            }
          })
        })
      
    }
  }
  
}
