import { addEmployee, queryEmployeeById, } from '@/api/employee'

export default {
  data() {
    return {
      optType: '', //the current operation is add or update
      ruleForm: {
        name: '',
        username: '',
        sex: '1',
        phone: '',
        idNumber: '',
      },
      rules: {
        name: [
          {
            required: true,
            message: 'Please enter employee name',
            trigger: 'blur',
          },
        ],
        username: [
          {
            required: true,
            message: 'Please enter employee username',
            trigger: 'blur',
          },
        ],
        phone: [
          {
            required: true,
            trigger: 'blur',
            validator: (rule, value, callback) => {
              if (value === '' || !/^1(3|4|5|6|7|8)\d{9}$/.test(value)) {
                callback(new Error('Please enter a correct phone number!'))
              } else {
                callback()
              }
            },
          },
        ],
        idNumber: [
          {
            required: true,
            trigger: 'blur',
            validator: (rule, value, callback) => {
              if (
                value === '' ||
                !/(^\d{15}$)|(^\d{18}$)|(^\d{17}(x|X)$)/.test(value)
              ) {
                callback(new Error('Please enter a correct id number!'))
              } else {
                callback()
              }
            },
          },
        ],
      },
    }
  },
  created() {
    //get the router parameter(id), if exists, it is update, otherwise add
    this.optType = this.$route.query.id ? 'update' : 'add'
    if (this.optType === 'update') {
      queryEmployeeById(this.$route.query.id).then((res) => {
        this.ruleForm = res.data.data
      })
    }
  },
  methods: {
    submitForm(formName, isContinue) {
      //check the form valid or not
      this.$refs[formName].validate((valid) => {
        if (valid) {
          //send ajax request to submit data to backend
          //add operation
          if (this.optType === 'add') {
            addEmployee(this.ruleForm).then((res) => {
              if (res.data.code === 1) {
                this.$message.success('Added Employee Successfully!')
                if (isContinue) {
                  this.ruleForm = {
                    name: '',
                    username: '',
                    sex: '1',
                    phone: '',
                    idNumber: '',
                  }
                } else {
                  this.$router.push('/employee')
                }
              } else {
                this.$message.error(res.data.message)
              }
            })
          }else {
            //update operation 

          }
        }
      })
    },
  },
}
